package main

import (
	"bytes"
	"testing"
)

func Test1000(t *testing.T) {
	want := ""
	var got bytes.Buffer
	pi(1000, &got)
	if want != got.String() {
		t.Errorf("Want: %v\nGot: %v", want, got.String())
	}
}

func Benchmark100(b *testing.B) {
	for i := 0; i < b.N; i++ {
		var buf bytes.Buffer
		pi(100, &buf)
	}
}
